import {createStore} from 'vuex'

export default createStore({ 
    state: { authenticated: false, searchQuery: '' }, 
    mutations: { 
        SET_AUTH: function (state, auth) { state.authenticated = auth },
        SET_SEARCH_QUERY: function (state, searchQuery) { state.searchQuery = searchQuery },
    }, 
    actions: { 
        setAuth: function ({commit}, auth) { commit('SET_AUTH', auth) },
        search: function ({commit}, searchQuery) { commit('SET_SEARCH_QUERY', searchQuery) },
    }, 
    getters: {
        searchQuery: function (state) { return state.searchQuery },
    },
    modules: {} 
})
